import React, {Component} from 'react';
import  {
    NetInfo,
    AppRegistry,
    StyleSheet,
    TouchableHighlight,
    ScrollView,
    Image,
    Text,
    TextInput,
    View,
    Alert,
    Modal,
    ListView,
    TouchableOpacity
    } from 'react-native';
var Proxy = require('../../proxy/Proxy');
import {connect} from 'react-redux';
import Config from '../../../config';
import MyConcernOffer from './MyConcernOffer';
import MyOffer from './MyOffer';
import Icon from 'react-native-vector-icons/FontAwesome';
import ScrollableTabView, {DefaultTabBar, ScrollableTabBar} from 'react-native-scrollable-tab-view';
import DatePicker from 'react-native-datepicker';
var Dimensions = require('Dimensions');
var {height, width} = Dimensions.get('window');
import Bigpic from './Bigpic'

class AllCompany extends Component {

    constructor(props) {
        super(props);
        this.state = {
            goodsCount: 0,
            start: 0,
            limit: 10,
            infoList: null,
            nubre: this.props.nubre,
            direccion: this.props.direccion,
            rubroDes: this.props.rubroDes,
            nomroDeTelePhono: this.props.nomroDeTelePhono,
            merchantId: this.props.merchantId,
            dataSource: new ListView.DataSource({
                rowHasChanged: (r1, r2) => {
                    if (r1 !== r2) {
                    } else {
                    }
                    return r1 !== r2;
                }
            })
        }
    }

    goBack() {
        const {navigator} = this.props;
        if (navigator) {
            navigator.pop();
            if (this.props.reset)
                this.props.reset();
        }
    }

    fetchData() {
        var start = this.state.start;
        var merchantId = this.state.merchantId;
        var limit = this.state.limit;
        Proxy.post({
            url: Config.server + "/func/merchant/getCommodityLabelListofSellerMobile",
            headers: {
                'Content-Type': 'application/json',
            },
            body: {
                sellerId: merchantId,
                start: start,
                limit: limit,
            }
        }, (json) => {
            var errorMsg = json.message;
            if (errorMsg !== null && errorMsg !== undefined && errorMsg !== "") {
                alert(errorMsg);
            } else {
                var infoList = json.data;
                this.setState({infoList: infoList});
            }
        })
    }

    renderRow(rowData) {
       // var base64Icon = 'data:image/png;base64,';
       // var img='data:image/jpeg;base64,';
        var row =
            <View>
                <TouchableOpacity onPress={() => {this.navigateBigpic(rowData.attachId)}}>
                    <View style={{
                    marginTop:50,marginRight:15,
                        flex: 1, padding: 10,borderLeftWidth:1, borderBottomWidth: 1, borderColor: '#ddd',
                        justifyContent: 'flex-start', backgroundColor: '#fff'
                    }}>
                        <Image resizeMode="stretch" style={{width:65,height:65}}
                               source={{uri:rowData.imgData}}
                               //source={{uri:base64Icon}}
                               //source={{uri:img}}
                            />
                    </View>
                    <View style={{padding:10}}>
                        <Text style={{fontsize:15}}>
                            价格:{rowData.precio}
                        </Text>
                    </View>
                </TouchableOpacity>
            </View>;
        return row;
    }

    laruguanzhu() {
        //var sessionId = this.props.sessionId;
        var state = 1;
        var merchantId = this.state.merchantId;
        Proxy.post({
            url: Config.server + '/func/merchant/setBuyerSellerStateMobile',
            headers: {
                'Content-Type': 'application/json',
                //'Cookie': sessionId,
            },
            body: {
                sellerId: merchantId,
                state: state,
            }
        }, (json) => {
            var errorMsg = json.errorMsg;
            if (errorMsg !== null && errorMsg !== undefined && errorMsg !== "") {
                alert(errorMsg);
            } else {
                alert("成功拉入关注");
            }
        }, (err) => {
            alert(err);
        });
    }

    larugongyingshang() {
        var state = 2;
        //var sessionId = this.props.sessionId;
         var merchantId = this.state.merchantId;
        Proxy.post({
            url: Config.server + '/func/merchant/setBuyerSellerStateMobile',
            headers: {
                'Content-Type': 'application/json',
                // 'Cookie': sessionId,
            },
            body: {
                sellerId: merchantId,
                state: state,
            }
        }, (json) => {
            var errorMsg = json.errorMsg;
            if (errorMsg !== null && errorMsg !== undefined && errorMsg !== "") {
                alert(errorMsg);
            } else {
                alert("成功拉入我的供应商");
            }
        }, (err) => {
            alert(err);
        });
    }

    navigateMyConcernOffer() {
        const {navigator} = this.props;

        if (navigator) {
            navigator.push({
                name: 'MyConcernOffer',
                component: MyConcernOffer,
                params: {}
            })
        }
    }

    navigateBigpic(attachId){
        const {navigator} = this.props;

        if (navigator) {
            navigator.push({
                name: 'Bigpic',
                component: Bigpic,
                params: {
                    attachId:attachId
                }
            })
        }
    }

    navigateMyOffer() {
        const {navigator} = this.props;

        if (navigator) {
            navigator.push({
                name: 'MyOffer',
                component: MyOffer,
                params: {}
            })
        }
    }

    render() {
        var listView = null;
        var infoList = this.state.infoList;
        if (infoList !== undefined && infoList !== null) {
            var data = infoList;
            var ds = new ListView.DataSource({rowHasChanged: (r1, r2) => r1 !== r2});
            listView =
                <ScrollView>
                    <ListView
                        horizontal={true}
                        showsHorizontalScrollIndicator={true}
                        padingEnable={true}
                        automaticallyAdjustContentInsets={false}
                        dataSource={ds.cloneWithRows(data)}
                        renderRow={this.renderRow.bind(this)}
                        />
                </ScrollView>;
        } else {
            this.state.infoList = [];
            this.fetchData();
        }

        return (
            <View style={{flex: 1}}>
                {/* header bar */}
                <View style={[{
                    backgroundColor: '#387ef5',
                    height: 55,
                    padding: 12,
                    flexDirection: 'row',
                    justifyContent: 'center',
                    alignItems: 'center',
                }, styles.card]}>
                    <View style={{flex: 1}}>
                        <TouchableOpacity onPress={() => {
                            this.goBack()
                        }}>
                            <Icon name="chevron-left" color="#fff" size={25}></Icon>
                        </TouchableOpacity>
                    </View>
                    <Text style={{fontSize: 22, flex: 3, textAlign: 'center', color: '#fff'}}>
                        {this.props.username}
                    </Text>
                    <View style={{flex: 1, marginRight: 10, flexDirection: 'row', justifyContent: 'center'}}>
                    </View>
                </View>
                <View style={{flex: 1}}>
                    <View style={{flex: 7, borderWidth: 1, borderColor: '#ddd'}}>
                        <View style={{flex: 1, flexDirection: 'row', borderWidth: 1, borderColor: '#ddd'}}>
                            <View style={{flex: 3}}>
                                <View style={{
                                flex:1,
                                    flexDirection: 'row',
                                    justifyContent: 'center',
                                    alignItems: 'center',
                                    paddingTop: 5
                                }}>
                                    <View style={{flex: 2}}>
                                        <Text style={styles.ziti}>公司名称：</Text>
                                    </View>
                                    <View style={{flex: 3, justifyContent: 'flex-start'}}>
                                        <Text style={styles.popoverText}>{this.state.nubre}</Text>
                                    </View>
                                </View>
                                <View style={{
                                flex:1,
                                    flexDirection: 'row',
                                    justifyContent: 'center',
                                    alignItems: 'center',
                                    paddingTop: 5
                                }}>
                                    <View style={{flex: 2}}>
                                        <Text style={styles.ziti}>公司地址：</Text>
                                    </View>
                                    <View style={{flex: 3}}>
                                        <Text style={styles.popoverText}>{this.state.direccion}</Text>
                                    </View>
                                </View>
                                <View style={{
                                flex:1,
                                    flexDirection: 'row',
                                    justifyContent: 'center',
                                    alignItems: 'center',
                                    paddingTop: 5
                                }}>
                                    <View style={{flex: 2}}>
                                        <Text style={styles.ziti}>公司营业范围：</Text>
                                    </View>
                                    <View style={{flex: 3}}>
                                        <Text style={styles.popoverText}>{this.state.rubroDes}</Text>
                                    </View>
                                </View>
                                <View style={{
                                flex:1,
                                    flexDirection: 'row',
                                    justifyContent: 'center',
                                    alignItems: 'center',
                                    paddingTop: 5
                                }}>
                                    <View style={{flex: 2}}>
                                        <Text style={styles.ziti}>公司联系电话：</Text>
                                    </View>
                                    <View style={{flex: 3}}>
                                        <Text style={styles.popoverText}>{this.state.nomroDeTelePhono}</Text>
                                    </View>
                                </View>
                            </View>
                            <View style={{flex: 1}}>
                                <TouchableOpacity style={styles.touchOty}
                                                  onPress={() => {
                                                      this.laruguanzhu()
                                                  }}>
                                    <Text style={{fontSize: 14}}>拉入关注</Text>
                                </TouchableOpacity>
                                <TouchableOpacity style={styles.touchOty}
                                                  onPress={() => {
                                                      this.larugongyingshang()
                                                  }}>
                                    <Text style={{fontSize: 14, padding: 2,paddingLeft:5}}>拉入我的供应商</Text>
                                </TouchableOpacity>
                                <TouchableOpacity style={styles.touchOty}>
                                    <Text style={{fontSize: 14}}>扫描</Text>
                                </TouchableOpacity>
                            </View>
                        </View>
                        <View style={{flex: 2}}>
                            <ScrollView>
                                {listView}
                            </ScrollView>
                        </View>
                    </View>
                    <View style={{flex: 1, flexDirection: 'row'}}>
                        <TouchableOpacity style={{
                            flex: 1,
                            justifyContent: 'center',
                            alignItems: 'center',
                            backgroundColor: '#CAE1FF',
                            marginLeft: 20,
                            marginRight: 20,
                            marginBottom: 20,
                            marginTop: 10,
                            borderRadius: 4,
                        }} onPress={() => {
                            this.navigateMyOffer()
                        }}>
                            <View>
                                <Text style={{fontSize: 16}}>我的供应商</Text>
                            </View>
                        </TouchableOpacity>
                        <TouchableOpacity style={{
                            flex: 1,
                            justifyContent: 'center',
                            alignItems: 'center',
                            backgroundColor: '#CAE1FF',
                            marginLeft: 20,
                            marginRight: 20,
                            marginBottom: 20,
                            marginTop: 10,
                            borderRadius: 4,
                        }} onPress={() => {
                            this.navigateMyConcernOffer()
                        }}>
                            <View>
                                <Text style={{fontSize: 16}}>我关注</Text>
                            </View>
                        </TouchableOpacity>
                    </View>
                </View>
            </View>
        );
    }
}


var styles = StyleSheet.create({
    card: {
        borderTopWidth: 0,
        borderBottomWidth: 1,
        borderBottomColor: 'rgba(0,0,0,0.1)',
        shadowColor: '#ccc',
        shadowOffset: {width: 2, height: 2,},
        shadowOpacity: 0.5,
        shadowRadius: 3,
    },
    ziti: {
        fontSize: 14,
        paddingLeft: 5,
    },
    touchOty: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: '#CAE1FF',
        borderRadius: 4,
        marginTop: 7,
        marginRight: 6,
        marginBottom: 3
    },
    popoverText: {
        fontSize: 14,
    },
});


module.exports = connect(state => ({
        username: state.user.username,
        sessionId: state.user.sessionId,
    })
)(AllCompany);